import React, { useState } from 'react';

function WelcomeScreen({ onLogin }) {
  const [nickname, setNickname] = useState('');

  const handleLogin = () => {
    if (nickname.trim()) {
      onLogin(nickname);
    } else {
      alert('Введите ваше имя!');
    }
  };

  return (
    <section className="welcome-section fade-in">
      {/* Левый блок — текст и форма */}
      <div className="welcome-content">
        <h1 className="big-title">Привет, мы — StoryForge.</h1>
        <p className="subtitle">
          Платформа для создания и прохождения уникальных историй.  
          Делитесь своим творчеством и исследуйте фантастические миры других авторов.
        </p>
        <div className="login-form">
          <input
            type="text"
            placeholder="Введите ваше имя..."
            value={nickname}
            onChange={(e) => setNickname(e.target.value)}
            className="input-field"
          />
          <button className="login-button" onClick={handleLogin}>
            Начать писать
          </button>
        </div>
      </div>

      {/* Правый блок — картинка */}
      <div className="welcome-image">
        <img src="/story-illustration." alt="Story illustration" />
      </div>
    </section>
  );
}

export default WelcomeScreen;
