import React, { useState } from 'react';

function StoryEditor() {
  const [storyTitle, setStoryTitle] = useState('');
  const [storyContent, setStoryContent] = useState('');
  const [savedStory, setSavedStory] = useState(null);

  const handleTitleChange = (event) => setStoryTitle(event.target.value);
  const handleContentChange = (event) => setStoryContent(event.target.value);

  const handleSave = () => {
    const newStory = { title: storyTitle, content: storyContent };
    setSavedStory(newStory);
    alert('Story saved: ' + storyTitle);
  };

  return (
    <div>
      <h2>Create a New Story</h2>
      <input
        type="text"
        placeholder="Enter story title"
        value={storyTitle}
        onChange={handleTitleChange}
      />
      <textarea
        placeholder="Write your story here..."
        value={storyContent}
        onChange={handleContentChange}
      />
      <button onClick={handleSave}>Save Story</button>
    </div>
  );
}

export default StoryEditor;
